package Package1;

public class bitwiseoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=9;
int b=12;
int c=a&b; //1000
System.out.println(c);
c=a|b;
System.out.println(c); //1101
c=a^b;
System.out.println(c);
c=~a;
System.out.println(c); //0110 
	}

}
